import { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: 0,
    };
  }

  increment = () => {
    this.setState((state) => ({
      counter: state.counter < 10 ? state.counter + 1 : state.counter,
      //counter: state.counter + 1,
    }));
  };

  decriment = () => {
    this.setState((state) => ({
      counter: state.counter < -10 ? state.counter - 1 : state.counter,
      //counter: state.counter - 1,
    }));
  };

  random = () => {
    this.setState((state) => ({
      counter: +(Math.random() * (10 - -10) + -10).toFixed(0),
    }));
  };

  reset = () => {
    this.setState((state) => ({
      counter: (state.counter = 0),
    }));
  };

  // counter:(counter<=10)?(state.counter + 1),state.counter,

  render() {
    // const { increment, decriment, random, reset } = this.props;
    return (
      <div className="app">
        <div className="counter">{this.state.counter}</div>
        <div className="controls">
          <button onClick={this.increment}>INC</button>
          <button onClick={this.decriment}>DEC</button>
          <button onClick={this.random}>RND</button>
          <button onClick={this.reset}>RESET</button>
        </div>
      </div>
    );
  }
}
export default App;
